import React from "react";
import Input from "./inputs/input";
import {BiSend} from "react-icons/bi";
import "./chatMsg.css";


class ChatMsg extends React.Component{


    constructor(props){
        super(props);
    }

    render(){
        return (
            <div className="chat-msg-container">
                <div className="contato-msg-header">

                </div>
                <div className="contato-msg-conteudo">
                    <ul>
                        {this.props.listaMsg ? this.props.listaMsg.map((v,i)=>{
                            if(v.remetente === this.props.user){
                                return <li className="msg-info remetente" key={i.toLowerCase()}><p>{v}</p></li>
                            }else{
                                return <li className="msg-info receptor" key={i.toLowerCase()}><p>{v}</p></li>
                            }   
                        }): null}
                    </ul>

                </div>
                <div className="contato-msg-entrada">
                    <div className="msg-entrada-container">
                        <Input placeHolder="escreva uma messagem..."/>
                    </div>
                    <BiSend className="icon-msg"/>
                </div>
            </div>
        )
    }
};

export default ChatMsg;
