import React from "react";
import ChatContatos from "./chatContatos";
import ChatMsg from "./chatMsg";
import SocketContext from "./context/socketContext";
import "./chat.css";

class Chat extends React.Component{

    constructor(props){
        super(props);
        this.state = {listaUser: [],indexUser: null};
        this.socket =  {};
        this.listaMsg = [];
        this.user = {};

        this.changeIndexMsg = this.changeIndexMsg.bind(this);
    }

    componentDidMount(){
        console.log(this.socket);
        this.user = "user";
        this.socket.on("novo user",(msg)=>{
            this.setState({listaUser: msg});
        })

        this.socket.on("user saiu",(msg)=>{
            this.setState({listaUser: msg});
        }) 
    }

    changeIndexMsg(index){
        this.setState({indexUser: index});
    }


    render(){
        return(
            <>
                <SocketContext.Consumer>
                    {(value)=>{this.socket = value}}
                </SocketContext.Consumer>
                <div className="chat-container">
                    <div className="chat-container-contatos">
                        <ChatContatos listaUser={this.state.listaUser} user={this.user} 
                        changeIndex={this.changeIndexMsg}/>
                    </div>
                    <div className="chat-container-msg">
                        <ChatMsg listaMsg={this.state.indexUser ? this.listaMsg.at(this.state.indexUser): null} 
                        user={this.user}/>
                    </div>
                </div> 
            </>
        )
    }

};

export default Chat;
