import React from "react";
import { io } from "socket.io-client";
import { Navigate } from "react-router-dom";
import "./login.css";


class Login extends React.Component{

    constructor(props){
        super(props);
        this.state = {nome: "",conexcao: false};
        this.changNome = this.changNome.bind(this);
        this.loginEvento = this.loginEvento.bind(this);
        this.socket = {};
    }

    changNome(e){
        this.setState({nome: e.target.value});

    }

    loginEvento(e){
        e.preventDefault();

        if(this.state.nome && this.state.nome !== ""){
           this.socket = io("ws://localhost:8080/",{auth: {nome: this.state.nome}});

           this.props.changeSocket(this.socket);
           this.setState({conexcao: true}); 
        }
    }

    render(){

        return(
            <>
                {this.state.conexcao ? <Navigate to={"../chat"}/>: null}
                <div className="login-container">
                    <form method="POST">
                        <label className="login-info">Nome:
                            <input className="login-entrada" type="text" required={true} name="nome" id="nome" value={this.state.nome} onChange={this.changNome}/>
                        </label>
                        <input className="login-submit" type="submit" value="Entrar" onClick={this.loginEvento}/>     
                    </form>
                </div>
            </>
        )
    }

};

export default Login;
