import Chat from './components/chat';
import Login from './components/login';
import { createBrowserRouter,RouterProvider } from 'react-router-dom';
import React from 'react';
import SocketContext from './components/context/socketContext';
import './App.css';


class App extends React.Component {

  constructor(props){
    super(props);

    this.router = new createBrowserRouter([{
      path: "/",
      children: [{
        path: "/",
        element: <Login/>
      },
      {
        path: "/chat",
        element: <Chat/>
      }  
    ]
    }]);

    this.socket = "teste";
    this.contexto = {socket: this.socket,changeSocket: this.changeSocket};
    this.changeSocket = this.changeSocket.bind(this);
  }

  changeSocket(novoSocket){
    this.socket = novoSocket;
  }


  render(){
    return(
      <SocketContext.Provider value={this.context}>
        <RouterProvider router={this.router}/>
      </SocketContext.Provider>
    )
  }
}

export default App;
